usage_example:
// Get ACP Protocol
const protocol = await bus_get_acp_protocol({format: 'markdown'});

// Request Consensus
const result = await bus_request_consensus({
  agent_id: 'coordinator',
  session_id: 'session-789',
  proposal: 'Migrate to API v2.0',
  target_agents: ['worker-1', 'worker-2', 'worker-3'],
  quorum: 2,
  voting_deadline_seconds: 120
});

// Validate Message
const validation = await bus_validate_acp_message({
  message: {
    type: 'STATUS_UPDATE',
    from_agent: 'worker-1',
    from_session: 'session-123',
    timestamp: new Date().toISOString(),
    payload: {status: 'processing', progress: 0.75}
  }
});
